Create table EventItem (EventItemID bigint primary key identity(1,1),EventInfoID bigint not null FOREIGN KEY REFERENCES EventInfo(EventInfoID),CategoryID bigint not null,CategoryValue varchar(150),ItemID bigint not null,ItemValue varchar(150),Quantity int not null,Price money not null,CreatedBy int not null,CreatedOn datetime not null,ModifiedBy int,ModifiedOn datetime,Status bit default(1))